import React from 'react'
import classes from './Bar.module.css'

class Bar extends React.Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.cityValue = this.props.cityValue
        this.changeCityValue = this.props.changeCityValue
    }

    handleChange(e) {
        e.preventDefault();
        console.log(`Была нажата клавиша. ${this.cityValue}`);
        this.changeCityValue(e.target.value)
    }

    render() {
        console.log(`render ${this.cityValue}`)
        return (
            <form className="d-flex">
                <input className="form-control me-2" type="search" placeholder="Search" aria-label="Search"
                       onChange={this.changeCityValue}/>
                <button className="btn btn-outline-success" type="submit" onClick={this.handleChange}>Search</button>
            </form>
        )
    }
}


export default Bar;
